/* ----------------------------------------------------------------------------------------------------------------------------	*/
/* 
	USU - Tabela de Usuarios 
	------------------------
*/
	create table tb00Usu
		(
		cpUsuLogin       	char(14)	not null,               -- Login do Usuario (KEY)
		cpUsuNome        	char(40)	not null default(''),   -- Nome
		cpUsuSenha 	     	char(10)	not null default(''),   -- Senha
		cpUsuReg 	     	char(02)	not null default(''), 	-- Regional (FK)
		cpUsuEmp		char(02)	not null default(''),   -- Empresa Padr�o do Usu�rio (Quando Usado em Distribuidora - Modulo 20) (FK)
		cpUsuRep		char(05)	not null default(''),   -- Representante Padr�o do Usu�rio (Quando Usado em Notebook - Modulo 24) (FK)
		cpUsuDtInat 	     	char(08)	not null default(''), 	-- Data da Inativacao do Usuario
		cpUsuUsu 	     	char(14)	not null default(''), 	-- Usuario que Inativou
		cpUsuMultConex		char(01)	not null default('N'),	-- Aceita Multiplas Conecc�es (S/N)
		cpUsuMsg		int		not null default(0)	-- Controle Mensagens novas para o usu�rio (0-Vazio,1-Possui Mensagem)
		)
	go

	-- Chave Prim�ria (KEY)
	create unique nonclustered index idxUsu01
		on tb00Usu (cpUsuLogin)
	go
	-- Chaves Estrangeiras (FK)
	create nonclustered index idxUsu02
		on tb00Usu (cpUsuReg)
	create nonclustered index idxUsu03
		on tb00Usu (cpUsuUsu)
	create nonclustered index idxUsu04
		on tb00Usu (cpUsuEmp)

	go

	-- Insert Padrao
	insert tb00USU values ('UADM01','ADMINISTRADOR DO SISTEMA GERENTE','UADM01','','','','','','N')

	go

/*
	Descricao:	Cadastro de usuarios do sistema. Abaixo eh criado um usuario ADMIN para dar 'partida' no sistema de 
	--------- 	gerencia.
*/
/* ----------------------------------------------------------------------------------------------------------------------------	*/
/* 
	UMD - Tabela de Usuarios X Modulo(s)
	------------------------------------
*/
	create table tb00Umd
		(
		cpUmdUsu       		char(14)	not null,       -- Usuario (KEY,FK)
		cpUmdMod 	     	char(02)	not null, 	-- Modulo (KEY,FK)
		)
	go

	-- Chave Prim�ria (KEY)
	create unique nonclustered index idxUmd01
		on tb00Umd (cpUmdUsu,cpUmdMod)

	-- Chaves Estrangeiras (FK)
	create nonclustered index idxUmd02
		on tb00Umd (cpUmdUsu)
	create nonclustered index idxUmd03
		on tb00Umd (cpUmdMod)

	go

	-- Insert Padrao
	insert into tb00Umd (cpUmdUsu,cpUmdMod) values ('UADM01','01')

