/* 
	Descricao:	Cadastro de Transportadores
	---------	

*/
/* ----------------------------------------------------------------------------------------------------------------------------	*/
/* 
	CCG - Tabela de Grupo do Plano de Contas
	----------------------------------------
*/
	create table tbCcg
                (
		cpCcgTipo	       	char(01)	not null,		-- Tipo (KEY,COMBO) 
		cpCcgCod	       	char(02)	not null,		-- Codigo Sequencial (KEY)
		cpCcgDesc       	char(25)	not null default('')	-- Descricao
		)
	go

	-- Chave Primaria (KEY)	
	create unique nonclustered index idxCcg01
		on tbCcg (cpCcgTipo, cpCcgCod)


	go

/* 
	Descricao dos Campos (COMBO):	
		
            cpCcgTipo -> 1-Ativo,2-Passivo,3-Custo,4-Receita		
*/
/* 
	Descricao:	Cadastro de C.Custo -> Grupo do C.Custo (Atual Plano de Contas)
	---------	
*/
/* ----------------------------------------------------------------------------------------------------------------------------	*/
/* 
	CCS - Tabela de Subgrupo do Plano de Contas
	-------------------------------------------
*/
	create table tbCcs
                (
		cpCcsTipo	       	char(01)	not null,		-- Tipo (KEY,FK,COMBO)
		cpCcsCcg       		char(02)	not null,		-- Grupo do C.Custo (KEY,FK)
		cpCcsCod	       	char(03)	not null,		-- Codigo Sequencial (KEY)
		cpCcsDesc       	char(25)	not null default('')	-- Descricao
		)
	go
        -- Chave Primaria (KEY)
	create unique nonclustered index idxCcs01
		on tbCcs (cpCcsTipo, cpCcsCcg, cpCcsCod)

	-- Chaves Estrangeiras (FK)	
	create nonclustered index idxCcs02
		on tbCcs (cpCcsTipo, cpCcsCcg)	

	go

	-- Constraint's 
	-- ************
	alter table tbCcs add constraint fkCcs01 foreign key (cpCcsTipo, cpCcsCcg)
	references  tbCcg (cpCcgTipo, cpCcgCod)
	go

/* 
	Descricao dos Campos (COMBO):	
		
            cpCcgTipo -> Ver Detalhes na Tabela tbCcg
*/
/* 
	Descricao:	Cadastro de C.Custo -> Subgrupo do C.Custo (Atual Plano de Contas)
	---------	
*/
/* ----------------------------------------------------------------------------------------------------------------------------	*/
/* 
	CSG - Tabela de Contas de Subgrupo do Plano de Contas
	-----------------------------------------------------
*/
	create table tbCsg
                (
		cpCsgCod		char(04)	not null,		-- Codigo da Conta (KEY)
		cpCsgTipo		char(01)	not null,		-- Tipo (FK,COMBO)
		cpCsgCcg		char(02)	not null,		-- Grupo do C.Custo Grupo (FK)
