		cpPtpMsgF02   	char(40)   	not null default(''),	-- Mensagem de NF para Fora do Estado 02
		cpPtpMsgF03   	char(40)   	not null default(''),	-- Mensagem de NF para Fora do Estado 03
		cpPtpPrcBS   	char(01)   	not null default('V'),	-- Preco Base (*)
		cpPtpDescTab   	char(01)   	not null default('N'),	-- Aplica Desconto da Tabela de Precos (*)
		cpPtpDescCan   	char(01)   	not null default('N'),	-- Aplica Desconto do Canal do Cliente (*)
		cpPtpBlqOco   	char(01)   	not null default('S'),	-- Grava Bloqueio de Ocorrencia (*)
		cpPtpPdAbMin   	char(01)   	not null default('N'),	-- Aceita Pedido Abaixo do Valor Minimo (*)
		cpPtpTemIpi   	char(01)   	not null default('N'),	-- Possui IPI (*)
		cpPtpDigPrcPed	int		not null default(1),    -- Como se trata digitacao de preco/desconto no pedido (1-Nao digita nada,2-Digita Preco Final,3-Digita Percentual de Desconto)
		cpPtpReplProxIt	char(01)	not null default('N'),  -- (Quando cpPtpDigPrcPed = 3) Solicitar se Replica Desconto Sim/Nao
		cpPtpLayOutNF	char(02)	not null default('01'),	-- Lay-Out da Nota Fiscal (01-Layout1,02-LayOut2)
		cpPtpImprNF	char(01)	not null default(''),	-- Imprimir Numera��o de Nota Fiscal (S/N)
		cpPtpDataAlt	char(08)	not null default(''),	-- Data da Ultima Altera��o do Registro 
