/* 
	COMBO:          MeioCob: 1-Carteira (Controle), 2-Direta, 3-Simples (Banc�ria), 4-Duplicata
        ---------	StaCont: 1-Banco, 2-Caixa, 3-Cheque-Pr�, 4-Outros

	Descricao:	Cadastro de Bancos
	---------	

*/
/* ----------------------------------------------------------------------------------------------------------------------------	*/
/* 
	REC - Contas a Receber
	----------------------
*/


	create table tbRec
                (
		cpRecCod    	char(06)	not null,		-- Numero do AR (KEY)
		cpRecCli   		char(05)	not null,		-- Codigo do Cliente (FK)
		cpRecRep   		char(05)	not null,		-- Representante (FK)
		cpRecRepN   	char(05)	not null,		-- Representante Novo (= cpRecRep a Principio, na ocasiao do representante original sair da empresa, o representante novo sera o responsavel pelo titulo) (FK)
		cpRecCsg  		char(04)	not null,		-- Conta do Subgrupo do Plano de Contas (FK)
		cpRecEmp 		char(02) 	not null,   		-- Empresa (FK)
		cpRecUsu 		char(14) 	not null,   		-- Usuario que gerou AR (FK)
		cpRecUsuBx 		char(14) 	not null default(''),	-- Usuario que baixou AR (FK)
		cpRecBco    	char(03)	not null default(''),	-- Banco do AR, Gravado pela Cobranca ou Pela Baixa no Caixa (FK)
		cpRecPrr    	char(02)	not null default(''),	-- Convenio (FK)
		cpRecMcx    	char(06)	not null default(''),	-- Movimento de Caixa que Baixou o AR (FK)
		cpRecPed   		char(07)	not null default(''),	-- Codigo do Pedido que Originou (FK) 
		cpRecSeq   		char(05)	not null default(''),	-- Sequencial do Pedido que Originou (FK)
		cpRecDup   		char(10)	not null default(''),	-- Numero da Duplicata
		cpRecNf   		char(08)	not null default(''),	-- Numero da Nota Fiscal 
		cpRecExtNf		char(01)	not null default(''),	-- Extensao da Nota Fiscal
		cpRecNsNum  	char(20)	not null default(''),	-- Nosso Numero
		cpRecNsNumImp	char(20)	not null default(''),	-- Nosso Numero (Banco Correspondente)
		cpRecValor   	money    	not null,		-- Valor do AR (Com Descontos, Exceto cpRecDescPed,cpRecDescFre,cpRecDescLog)
		cpRecVlReal    		money    	not null,		-- Valor Real do AR (Sem Descontos, Cheio, Original)
		cpRecValRec   		money    	not null default(0 ),	-- Valor Recebido (Devera ser o Valor do AR = cpRecValor)
		cpRecValRecR   		money    	not null default(0 ),	-- Valor Real Recebido (com Mora ou Descontos: cpRecDescPed,cpRecDescLog,cpRecDescFre)
		cpRecVlMora    		money    	not null default(0 ),	-- Valor da Mora diaria (Existe um Percentual no Parametro do Sistema - cpParMora)
		cpRecSaldo     		money    	not null,		-- Valor a ser recebido do titulo (no recebimento por conta, sera abatido aos poucos)
		cpRecDescPed 		money		not null default(0 ),	-- % Desconto do Pedido (Aplica-se ao cpRecValor)
		cpRecDescLog 		money		null default(0 ),	-- % Desconto da Logistica (Aplica-se ao cpRecValor apos o cpRecDescPed)
		cpRecDescFre 		money		not null default(0 ),	-- % Desconto do Frete (Aplica-se ao cpRecValor apos o cpRecDescLog)
