/* ----------------------------------------------------------------------------------------------------------------------------	*/
/* 
	OCV - Ocorrencia do Pedido de Venda
	-----------------------------------
*/
	create table tbOcv
                (
		cpOcvPed 	     	char(07)	not null,		-- Codigo (KEY,FK)
		cpOcvOcor		char(02)	not null,		-- Ocorrencia (KEY) *
		cpOcvStatus		char(01)	not null default(''),	-- Status (1-Em Aberto,2-Liberado,3-Cancelado)
		cpOcvUsu		char(14)	not null default(''),	-- Usu�rio da Libera��o/Cancelamento 
		cpOcvData		char(08)	not null default(''),	-- Data da Libera��o/Cancelamento
		cpOcvHora 		char(05) 	not null default(''), 	-- Hora da Libera��o/Cancelamento
		cpOcvStatus		char(01)	not null default(''),	-- Status (1-Em Aberto,2-Liberado,3-Cancelado)
		cpOcvStaAux		char(01)	not null default('')	-- Status auxiliar (S� Quando Ocorrencia = "04") * 
	 	)
	go


	-- Chave Primaria (KEY)
	create unique nonclustered index idxOcv01
		on tbOcv (cpOcvPed, cpOcvOcor)
	go
	-- Chave Estrangeira (FK)
	create nonclustered index idxOcv02
		on tbOcv (cpOcvPed)
/* 
	Descricao: 	Ocorrencia de Bloqueio do Pedido de Vendas.
	---------				
	
	cpOcvOcor	->	01 - Limite de Cr�dito (Cobranca)
				02 - Custo Regional (Vendas)
				03 - Custo Nacional (Vendas)
				04 - Inadimplencia (Cobranca)
				05 - Desconto (Vendas)
				06 - Bonificacao (Cobranca)
				07 - Simples Remessa (Merchandising)
				08 - Prazo (Vendas)

	cpOcvStaAux	->	1-Inadimplencia
				2-Ch.Dev
				3-Rec. S/ Juros
				4- 1 + 2
				5- 1 + 3
				6- 2 + 3
				7- 1 + 2 + 3

*/
/* ----------------------------------------------------------------------------------------------------------------------------	*/
