		cpInfCustoConv		money		not null default(0),	-- Custo Unitario em Unidades
		cpInfBaseSubst		money		not null default(0),	-- Base da Substitui��o Tribut�ria
		cpInfValSubst		money		not null default(0),	-- Valor da Substitui��o Tribut�ria
		cpInfValRedBsIcms	money		not null default(0),	-- Valor da Redu��o da Base do ICMS
		cpInfPrFrete		money		not null default(0)	-- Preco do Frete Unit�rio
	 	)
	go

