/* 
        COMBO:     cpCftTipo.:  1-Produto Final,2-Sub Produto,3-Conjunto,4-F�rmula
        ---------  cpCftAtiva:  S-Sim, N-Nao

	Descricao: Tabela de Cabecalho da Ficha Tecnica.
	---------  cpCftPro assume um codigo de Produto quando o valor do campo cpCftTipo for <> de 2.
		   cpCftPro assume um codigo de Insumo quando o valor do campo cpCftTipo for = a 2.
*/
/* ----------------------------------------------------------------------------------------------------------------------------	*/
/* 
	IFT - Itens da Ficha Tecnica
	----------------------------
*/
	create table tbIft
                (
		cpIftCod	       	char(06)	not null,		-- Codigo da Ficha (KEY,FK)
		cpIftSeq	       	char(03)	not null,		-- Sequencial da Ficha (KEY,FK)
		cpIftSqc	       	char(05)	not null,		-- Sequencial do Item (KEY)
        	cpIftSta   		int	   	not null default(0 ),	-- Status do Item 
		cpIftIns	       	char(05)	not null default(''),	-- Item, Se Sta=1: Codigo do Insumo, Se Sta=5: Codigo do PA, Senao "" (FK)
		cpIftDesc	       	char(20)	not null default(''),	-- Descricao (Se Sta=2)
		cpIftQtd	       	decimal(28,9)	not null default(0 ),	-- Quantidade
		cpIftValor	       	decimal(28,9)	not null default(0 ),	-- Valor (Se Sta <> 1 e 5)
		cpIftPerc	       	decimal(28,9)	not null default(0 ),	-- Percentual (Se Sta <> 1 e 5)
		cpIftStapl	       	char(01)	not null default(''),	-- Aplicacao do Percentual (COMBO)
		cpIftPerBs 		money 		not null default(0 ) 	-- % Sobre Peso Basico (Sta=1)
		)
	go

