		cpOpdGerLd              char(01)	not null default('N'), 	-- Status que indica se esta OP gerou laudo de Estabilidade do Produto (*) 
		cpOpdVendido        	decimal(28,9)	not null default(0 ),	-- Quantidade Vendida/Utilizada do Produto/Insumo da OP (Em Unidades/Quilo)
		cpOpdUsaRes	        char(01)	not null default(''),	-- Utiliza Reserva (*)
		cpOpdUsu	       	char(14)	not null default(''),	-- Usuario que Criou (FK)
		cpOpdEmp 		char(02)	not null default(''),	-- Empresa (FK)
		cpOpdEst		char(03)	not null default(''),	-- Estoque (FK)
		cpOpdDtVal		char(08)	not null default(''),	-- Data da Validade OP
		cpOpdContinua		char(01) 	not null default('N'),	-- OP Continua(S/N)
		cpOpdEqp		char(05)    	not null default('')	-- Equipamento(FK)
		)
