		cpItvPreco		money		not null default(0),	-- Preco
		cpItvPrDes		money		not null default(0),	-- Preco com Desconto
		cpItvPrNota		money		not null default(0),	-- Preco de Nota (C/ Desc Tab, Canal e Item)
		cpItvIpi		money		not null default(0),	-- Percentual de IPI
		cpItvIcms		money		not null default(0),	-- Percentual de ICMS
		cpItvPercomis		money		not null default(0),	-- Percentual da Comissao (Representante)
		cpItvPerComSup		money		not null default(0),	-- Percentual da Comissao (Supervisor)
		cpItvValdesc		money		not null default(0),	-- Valor do desconto
		cpItvValipi		money		not null default(0),	-- Valor do IPI
