		cpItvValcomis		money		not null default(0),	-- Valor da Comissao (Representante)
		cpItvValComSup		money		not null default(0),	-- Valor da Comissao (Supervisor)
		cpItvQtdAtend		int		not null default(0),	-- Quantidade Atendida
		cpItvValor		money		not null default(0),	-- Valor sem impostos e desconto
		cpItvValFinal		money		not null default(0),	-- Valor Final
		cpItvQtConv		int		not null default(0),	-- Quantidade Convertida (**)
		cpItvPreComis		money		not null default(0),	-- Premio por Unidade Vendida do Produdo
		cpItvDescTab		money		not null default(0),	-- Desconto Tabela (Vem da Tabela de Precos)
		cpItvDescCan		money		not null default(0),	-- Desconto Canal (Vem do Canal do Cliente)
		cpItvDescPed		money		not null default(0),	-- Desconto do Pedido (Condicional)
		cpItvDescLog		money		not null default(0),	-- Desconto de Log�stica (Condicional)
		cpItvDescFre		money		not null default(0),	-- Desconto de Frete (Condicinal)
		cpItvDescItm		money		not null default(0),	-- Desconto de Item
		cpItvDescFim		money		not null default(0),	-- Desconto de Final do Pedido
		cpItvCustoConv		money		not null default(0),	-- Custo em Unidades (**)
		cpItvBaseSubst		money		not null default(0),	-- Base da Substitui��o Tribut�ria		
		cpItvValSubst		money		not null default(0),	-- Valor da Substitui��o Tribut�ria
		cpItvValRedBsIcms	money		not null default(0),	-- Percentual de Reducao na Base de Calculo do ICMS 
		cpItvValMinimo		money		not null default(0),	-- Valor M�nimo
		cpItvPrFrete		money		not null default(0),	-- Preco do Frete 
		cpItvPrDesINT		money		not null default(0)	-- Preco com Desconto (Em Outra Moeda)
	 	)
	go

