*/
	create table tbPbc
                (
		cpPbcBco		char(03)	not null,		-- Banco (KEY,FK)
		cpPbcPrr		char(02)	not null,		-- Codigo (KEY,FK)
		cpPbcBcc		char(03)	not null default(''),	-- Identifica��o - Banco Nacional (FK)
