/*

	Descri��o:

*/
/* ----------------------------------------------------------------------------------------------------------------------------	*/
/* 
	TPT - Tabela de Precos de Terceiriza��o
	---------------------------------------
*/
	create table tbTpt
                (
		cpTptCod	       	char(06)	not null,		-- Codigo  (KEY) 
		cpTptCli         	char(05)    	not null default(''),	-- Cliente (FK)
		cpTptPrz	       	char(03)    	not null default(''),	-- Prazo   (FK) 
		cpTptDescr         	char(40) 	not null default(''),	-- Descri��o
		cpTptDtInic		char(08)	not null default(''),	-- Data Inicial da Validade da Tabela
		cpTptDtFim		char(08)	not null default(''),	-- Data Final da Validade da Tabela
		cpTptDtInativ		char(08)	not null default(''),	-- Data da Inativa��o da Tabela de Pre�os
		cpTptData         	char(08) 	not null default(''),	-- Data
		cpTptHora		char(05)	not null default(''),	-- Hora
		cpTptLogin		char(14)	not null default(''),	-- Login   (FK)
		cpTptVigente		char(01)	not null default('N')	-- Tabela Vigente (S/N)
		)
	go

	-- Chave Primaria (KEY)
	create unique nonclustered index idxTpt01
		on tbTpt (cpTptCod)

	-- Chaves Estrangeiras (FK)
	create nonclustered index idxTpt02
		on tbTpt (cpTptCli)
	create nonclustered index idxTpt03
		on tbTpt (cpTptPrz)
	create nonclustered index idxTpt04
		on tbTpt (cpTptLogin)
	go

	-- Cria��o de Triggers 
	-- Verifica se deseja tornar a tabela atual como VIGENTE, a partir da� torna as outras como n�o vigente.

	create trigger tgTpt01 
	on tbTpt for update, Insert
	as 
	   declare @CodTab  char(06),
	   	   @Vigente char(01),
	           @Cliente char(05)
	   select @CodTab  = cpTptCod, 
	          @Vigente = cpTptVigente,
	          @Cliente = cpTptCli 
	   from inserted
	   if update(cpTptVigente) and @Vigente = 'S'  
	   begin	
	      update tbTpt 
	      set cpTptVigente='N' 
	      where cpTptCli = @Cliente 
	        and cpTptCod <> @CodTab
		and cpTptVigente = 'S'
	   end
	GO

